  useEffect(() => {
    const fetchPosts = async () => {
      const res = await axios.get("/posts");   //  data coming from Backend Port : localhost:5000/api/posts
      console.log("res.data:", res.data); //All response data
      setPosts(res.data);
      // console.log(posts.map((m) => m.title)); //All Titles
    };
    fetchPosts();
  }, []);

  return (
    <>
      <Header />
      <div className="home">
        <Posts posts={posts} />
        <Sidebar />
      </div>
    </>
  );
}

export default Home;
